const file = components.filepicker1.file[0];
const fileExtension = file.name.slice(file.name.lastIndexOf("."));
const fileName = `lms/img_${moment().valueOf()}${fileExtension}`;

return { fileName };

const data = queries.fetchLeads.data;

// Function to format numbers with suffixes (e.g., 1.5K)
function formatNumber(num, decimalPlaces = 0) {
  if (num == Infinity) return num;

  const suffixes = ["", "K", "M", "B"];
  let index = 0;

  while (num >= 1e3 && index < suffixes.length - 1) {
    num /= 1e3;
    index++;
  }

  return num.toFixed(decimalPlaces) + suffixes[index];
}

// Get the current month and the previous month
const thisMonth = moment();
const lastMonth = moment().subtract(1, "month");

// Initialize an object to store state-wise data
const stateWiseData = {
  Lead: [],
  Opportunity: [],
  Customer: [],
  Lost: [],
};

// Function to convert NaN value to 0
function convertNanToZero(num) {
  return isNaN(num) ? 0 : num;
}

// Function to convert Infinity value to N/A and Format
function convertInfinityToNaAndFormat(num, decimalPlaces = 0) {
  return isFinite(num) ? formatNumber(num, decimalPlaces) : "N/A";
}

// Function to determine if a value is negative or positive
function isNegativeOrPositive(value) {
  return value <= 0 ? "negative" : "positive";
}

// Function to calculate percentage difference between current and previous values
function calculateDiff(curr, prev) {
  let diff = ((curr - prev) / prev) * 100;
  diff = convertNanToZero(diff);
  return parseFloat(diff.toFixed(2));
}

let currLeads = 0;
let currDeals = 0;
let currAmount = 0;
let prevLeads = 0;
let prevDeals = 0;
let prevAmount = 0;

data.forEach((row) => {
  stateWiseData[row.status].push(row);

  const isCustomer = row.status === "Customer";

  if (moment(row.created_at).isSame(thisMonth, "month")) {
    currLeads++;
    if (isCustomer) {
      currDeals++;
      currAmount += row.deal_amount;
    }
  } else if (moment(row.created_at).isSame(lastMonth, "month")) {
    prevLeads++;
    if (isCustomer) {
      prevDeals++;
      prevAmount += row.deal_amount;
    }
  }
});

// Calculate percentage differences
const totalDealsDiff = calculateDiff(currDeals, prevDeals);
const totalAmtDiff = calculateDiff(currAmount, prevAmount);
const conversionRateCurrMonth = convertNanToZero((currDeals / currLeads) * 100);
const conversionRatePrevMonth = convertNanToZero((prevDeals / prevLeads) * 100);
const conversionRateDiff = calculateDiff(
  conversionRateCurrMonth,
  conversionRatePrevMonth
);

// Create, format and return the result object
return {
  ...stateWiseData,
  totalDealsCurrMonth: convertInfinityToNaAndFormat(currDeals),
  totalDealsDiff: convertInfinityToNaAndFormat(totalDealsDiff, 2),
  totalDealsDiffSign: isNegativeOrPositive(totalDealsDiff),
  totalAmtCurrMonth: convertInfinityToNaAndFormat(currAmount, 2),
  totalAmtDiff: convertInfinityToNaAndFormat(totalAmtDiff, 2),
  totalAmtDiffSign: isNegativeOrPositive(totalAmtDiff),
  conversionRateCurrMonth: convertInfinityToNaAndFormat(
    conversionRateCurrMonth,
    2
  ),
  conversionRateDiff: convertInfinityToNaAndFormat(conversionRateDiff, 2),
  conversionRateDiffSign: isNegativeOrPositive(conversionRateDiff),
};

